{{--delete country image --}}    <script>        $(function () {            $('.removeImage').click(function () {                if (!confirm("Confirm Delete image ?")) {                    return false;                }                var that = $(this);                var id = $(this).attr('data-id');                console.log(id);                $.ajax({                    url: '{{action('AdminCountriesController@deleteCountryImage')}}',                    method: 'POST',                    data: {image_id: id}                }).done(function (response) {                    var data = $.parseJSON(response);                    if (data.status == 1) {                        that.fadeOut(500)//                        alert(data.message)                        $('.ajax').html(data.message)                    }                })                return false;            })        })    </script>@stop